import React, { useEffect, useState } from 'react';
import {
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Typography,
  Box,
  CircularProgress,
  Chip,
  TextField,
  Button,
  InputAdornment,
} from '@mui/material';
import { Search, LocalHospital, School, Security, SupportAgent } from '@mui/icons-material';
import axios from 'axios';

interface CommunityResource {
  id: number;
  title: string;
  description: string;
  category: string;
  contact_info: {
    phone?: string;
    email?: string;
  };
  url?: string;
  is_active: boolean;
}

const CommunityDashboard: React.FC = () => {
  const [resources, setResources] = useState<CommunityResource[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredResources, setFilteredResources] = useState<CommunityResource[]>([]);
  const [categoryFilter, setCategoryFilter] = useState('');

  useEffect(() => {
    // Fetch resources from API
    const fetchResources = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`${process.env.COMMUNITY_API_URL}/community-resources`);
        setResources(response.data);
        setFilteredResources(response.data); // Display all resources initially
      } catch (error) {
        console.error('Error fetching resources:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchResources();
  }, []);

  // Dynamically update the displayed resources based on search and filter
  useEffect(() => {
    const lowerCaseQuery = searchQuery.toLowerCase();
    const updatedResources = resources.filter((resource) => {
      const matchesSearch =
        resource.title.toLowerCase().includes(lowerCaseQuery) ||
        resource.description.toLowerCase().includes(lowerCaseQuery);
      const matchesCategory =
        !categoryFilter || resource.category.toLowerCase() === categoryFilter.toLowerCase();
      return matchesSearch && matchesCategory;
    });

    setFilteredResources(updatedResources);
  }, [searchQuery, categoryFilter, resources]);

  const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchQuery(event.target.value);
  };

  const handleCategoryChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCategoryFilter(event.target.value);
  };

  const getCategoryIcon = (category: string) => {
    switch (category.toLowerCase()) {
      case 'emergency':
        return <LocalHospital color="error" />;
      case 'education':
        return <School color="primary" />;
      case 'support':
        return <SupportAgent color="info" />;
      default:
        return <Security color="action" />;
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ px: 3, py: 5 }}>
      <Typography variant="h4" gutterBottom sx={{ fontWeight: 'bold', mb: 3 }}>
        Community Resources
      </Typography>

      {/* Search and Filter Section */}
      <Box sx={{ display: 'flex', gap: 2, alignItems: 'center', mb: 3 }}>
        <TextField
          label="Search"
          value={searchQuery}
          onChange={handleSearchChange}
          variant="outlined"
          fullWidth
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <Search />
              </InputAdornment>
            ),
          }}
        />
        <TextField
          label="Category Filter"
          value={categoryFilter}
          onChange={handleCategoryChange}
          variant="outlined"
        />
      </Box>

      {/* Resource List Section */}
      <List>
        {filteredResources.map((resource) => (
          <ListItem
            key={resource.id}
            sx={{
              mb: 2,
              p: 2,
              backgroundColor: '#fff',
              borderRadius: 1,
              boxShadow: '0 2px 6px rgba(0, 0, 0, 0.1)',
              '&:hover': {
                backgroundColor: '#f5f5f5',
              },
            }}
          >
            <ListItemIcon>{getCategoryIcon(resource.category)}</ListItemIcon>
            <ListItemText
              primary={
                <Typography variant="h6" sx={{ fontWeight: 600 }}>
                  {resource.title}
                </Typography>
              }
              secondary={
                <>
                  <Typography variant="body2" sx={{ mt: 1 }}>
                    {resource.description}
                  </Typography>
                  {resource.contact_info.phone && (
                    <Typography variant="body2">
                      <strong>Phone:</strong> {resource.contact_info.phone}
                    </Typography>
                  )}
                  {resource.contact_info.email && (
                    <Typography variant="body2">
                      <strong>Email:</strong> {resource.contact_info.email}
                    </Typography>
                  )}
                  {resource.url && (
                    <Typography variant="body2">
                      <strong>More Info:</strong>{' '}
                      <a href={resource.url} target="_blank" rel="noopener noreferrer">
                        Visit
                      </a>
                    </Typography>
                  )}
                </>
              }
            />
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

export default CommunityDashboard;
