import React, { useState, ReactNode, useEffect } from 'react';
import {
  Grid,
  Card,
  CardContent,
  Box,
  Typography,
  IconButton,
  Tooltip,
  Tabs,
  Tab,
  Chip,
  useTheme,
  LinearProgress,
  TextField
} from '@mui/material';
import {
  Refresh,
  NotificationsActive,
  Search,
  FilterList,
  MoreVert,
  KeyboardArrowUp,
  Warning
} from '@mui/icons-material';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip as ChartTooltip, ResponsiveContainer } from 'recharts';
import axios from 'axios';

const activityData = [
  { time: '00:00', incidents: 5 },
  { time: '04:00', incidents: 3 },
  { time: '08:00', incidents: 8 },
  { time: '12:00', incidents: 12 },
  { time: '16:00', incidents: 15 },
  { time: '20:00', incidents: 10 }
];

interface DashboardCardProps {
  title: string;
  action: ReactNode;
  children: ReactNode;
  priority?: 'normal' | 'high';
}

const DashboardCard = ({ title, action, children, priority = 'normal' }: DashboardCardProps) => {
  const theme = useTheme();

  return (
    <Card sx={{
      height: '100%',
      borderLeft: priority === 'high' ? `4px solid ${theme.palette.error.main}` : 'none',
      transition: 'transform 0.2s',
      '&:hover': {
        transform: 'translateY(-2px)',
        boxShadow: theme.shadows[4]
      }
    }}>
      <CardContent>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
          <Typography variant="h6" sx={{ fontWeight: 500 }}>
            {title}
          </Typography>
          {action}
        </Box>
        {children}
      </CardContent>
    </Card>
  );
};

const Dashboard = () => {
  const [timeRange, setTimeRange] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredData, setFilteredData] = useState(activityData);
  const [filterOptions, setFilterOptions] = useState({ category: '', severity: '' });
  const theme = useTheme();

  // Fetch filtered data from API
  const fetchFilteredData = async () => {
    try {
      const response = await axios.get(`${process.env.LAW_ENFORCEMENT_API_URL}/incidents`, {
        params: filterOptions
      });
      setFilteredData(response.data);
    } catch (error) {
      console.error('Error fetching filtered data:', error);
    }
  };

  useEffect(() => {
    fetchFilteredData();  // Load data initially
  }, [filterOptions]);

  const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchQuery(event.target.value);
  };

  const handleFilterChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setFilterOptions(prevState => ({
      ...prevState,
      [event.target.name]: event.target.value
    }));
  };

  return (
    <Box sx={{ p: 3 }}>
      {/* Header Section */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 4 }}>
        <Box>
          <Typography variant="h4" sx={{ fontWeight: 600, mb: 1 }}>
            Command Center
          </Typography>
          <Typography color="text.secondary">
            Real-time law enforcement operations overview
          </Typography>
        </Box>
        <Box sx={{ display: 'flex', gap: 1 }}>
          <Tooltip title="Active Incidents">
            <IconButton color="error">
              <NotificationsActive />
            </IconButton>
          </Tooltip>
          <Tooltip title="Search Records">
            <IconButton>
              <Search />
            </IconButton>
          </Tooltip>
          <Tooltip title="Refresh Data">
            <IconButton>
              <Refresh />
            </IconButton>
          </Tooltip>
        </Box>
      </Box>

      {/* Search and Filter Section */}
      <Box sx={{ mb: 3 }}>
        <TextField
          label="Search"
          value={searchQuery}
          onChange={handleSearchChange}
          variant="outlined"
          fullWidth
          sx={{ mb: 2 }}
        />
        <TextField
          label="Category"
          name="category"
          value={filterOptions.category}
          onChange={handleFilterChange}
          variant="outlined"
          sx={{ mr: 2 }}
        />
        <TextField
          label="Severity"
          name="severity"
          value={filterOptions.severity}
          onChange={handleFilterChange}
          variant="outlined"
        />
      </Box>

      {/* Time Range Selector */}
      <Tabs value={timeRange} onChange={(_, val) => setTimeRange(val)} sx={{ mb: 3 }}>
        <Tab label="Live View" />
        <Tab label="Last 24 Hours" />
        <Tab label="This Week" />
        <Tab label="This Month" />
      </Tabs>

      <Grid container spacing={3}>
        {/* Priority Incidents */}
        <Grid item xs={12} md={8}>
          <DashboardCard
            title="Active Incidents"
            priority="high"
            action={
              <Box sx={{ display: 'flex', gap: 1 }}>
                <Tooltip title="Filter">
                  <IconButton size="small">
                    <FilterList />
                  </IconButton>
                </Tooltip>
                <Tooltip title="More">
                  <IconButton size="small">
                    <MoreVert />
                  </IconButton>
                </Tooltip>
              </Box>
            }
          >
            <Box sx={{ height: 300 }}>
              <ResponsiveContainer width="100%" height="100%">
                <LineChart data={filteredData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="time" />
                  <YAxis />
                  <ChartTooltip />
                  <Line
                    type="monotone"
                    dataKey="incidents"
                    stroke={theme.palette.error.main}
                    strokeWidth={2}
                    dot={{ r: 4 }}
                    activeDot={{ r: 6 }}
                  />
                </LineChart>
              </ResponsiveContainer>
            </Box>
          </DashboardCard>
        </Grid>
        {/* Additional components for quick stats and critical alerts */}
      </Grid>
    </Box>
  );
};

export default Dashboard;
